mapper.select:
select , ee.equipment_code,ee.equipment_name,ee.equipment_desc,ee.equipment_model, eec.equipment_category_desc,bw.warehouse_name,ero.requisition_time, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_ht_requisition_order_det ehrod left join fantech_imes_v2.eam_equipment ee on ehrod.equipment_id=ee.equipment_id left join fantech_imes_v2.eam_equipment_category eec on ee.equipment_category_id=eec.equipment_category_id left join fantech_imes_v2.base_warehouse bw on ee.warehouse_id=bw.warehouse_id left join fantech_imes_v2.eam_requisition_order ero on ehrod.requisition_order_id=ero.requisition_order_id left join ocean_v2.sys_user u on ehrod.create_user_id=u.user_id left join ocean_v2.sys_user s on ehrod.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ehrod.org_id = bo.organization_id
and ee.equipment_code like CONCAT('%', #{equipmentCode}, '%')
and ee.equipment_name like CONCAT('%', #{equipmentName}, '%')
and ee.equipment_desc like CONCAT('%', #{equipmentDesc}, '%')
and ee.equipment_model like CONCAT('%', #{equipmentModel}, '%')
and eec.equipment_category_desc like CONCAT('%', #{equipmentCategoryDesc}, '%')
and date_format(ero.requisition_time, '%Y-%m-%d') = #{requisitionTime}
and date_format(ehrod.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehrod.status = #{status}
and ehrod.org_id=#{orgId}
order by ehrod.create_time desc
