mapper.select:
SELECT, mpwo.work_order_code as work_order_code,mpwo.work_order_status as work_order_status,bm.material_code as material_code, bpm.product_model_name as product_model_name,bpm.product_model_code as product_model_code,br.route_name as route_name, bf.factory_name as factory_name,bws.work_shop_name as work_shop_name,bpl.pro_name as pro_line_name FROM eam_ht_wi_release ehwr LEFT JOIN mes_pm_work_order mpwo ON ehwr.work_order_id = mpwo.work_order_id LEFT JOIN base_material bm ON ehwr.material_id = bm.material_id LEFT JOIN base_material_tab bmt ON ehwr.material_id=bmt.material_id LEFT JOIN base_product_model bpm ON bmt.product_model_id=bpm.product_model_id LEFT JOIN base_route br ON ehwr.route_id = br.route_id LEFT JOIN base_factory bf ON ehwr.factory_id = bf.factory_id LEFT JOIN base_work_shop bws ON ehwr.work_shop_id = bws.work_shop_id LEFT JOIN base_pro_line bpl ON ehwr.pro_line_id = bpl.pro_line_id LEFT JOIN ocean_v2.sys_user su ON ehwr.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON ehwr.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON ehwr.org_id = bo.organization_id
and ehwr.org_id = #{orgId}
and ehwr.wi_release_code = #{wiReleaseCode}
