mapper.select:
select , bm.material_code,bm.material_name,bpm.product_model_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_issue ei left join fantech_imes_v2.base_material bm on ei.material_id=bm.material_id left join fantech_imes_v2.base_material_tab bmt on ei.material_id=bmt.material_id left join fantech_imes_v2.base_product_model bpm on bmt.product_model_id=bpm.product_model_id left join ocean_v2.sys_user u on ei.create_user_id=u.user_id left join ocean_v2.sys_user s on ei.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ei.org_id = bo.organization_id
and ei.issue_code like CONCAT('%', #{issueCode}, '%')
and ei.issue_name like CONCAT('%', #{issueName}, '%')
and bm.material_code like CONCAT('%', #{materialCode}, '%')
and bm.material_name like CONCAT('%', #{materialName}, '%')
and bpm.product_model_name like CONCAT('%', #{productModelName}, '%')
and date_format(ei.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ei.status = #{status}
and ei.org_id=#{orgId}
order by ei.create_time desc
