mapper.select:
select , bm.material_code,bm.material_name,bm.material_desc,bm.material_version, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_jig_material_list ejml left join fantech_imes_v2.base_material bm on ejml.material_id=bm.material_id left join ocean_v2.sys_user u on ejml.create_user_id=u.user_id left join ocean_v2.sys_user s on ejml.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ejml.org_id = bo.organization_id
and ejml.jig_material_id = #{jigMaterialId}
and date_format(ejml.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ejml.status = #{status}
and ejml.org_id=#{orgId}
order by ejml.create_time desc

select , bm.material_code,bm.material_name,bm.material_desc,bm.material_version, ej.jig_code,ej.jig_name,ej.jig_desc,ej.jig_model, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_jig_material_list ejml left join fantech_imes_v2.base_material bm on ejml.material_id=bm.material_id left join fantech_imes_v2.eam_jig_material ejm on ejm.jig_material_id=ejml.jig_material_id left join fantech_imes_v2.eam_jig ej on ejm.jig_id=ej.jig_id left join ocean_v2.sys_user u on ejml.create_user_id=u.user_id left join ocean_v2.sys_user s on ejml.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ejml.org_id = bo.organization_id
and ejm.jig_id = #{jigId}
and ej.jig_code like CONCAT('%', #{jigCode}, '%')
and ej.jig_name like CONCAT('%', #{jigName}, '%')
and ej.jig_desc like CONCAT('%', #{jigDesc}, '%')
and ej.jig_model like CONCAT('%', #{jigModel}, '%')
and date_format(ejm.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ejm.status = #{status}
and ejm.org_id=#{orgId}
order by ejml.create_time desc
