mapper.select:
select , ej.jig_code,ej.jig_name,ej.jig_desc,ej.jig_model, CONCAT_WS(';',bw.warehouse_name,bwa.warehouse_area_name,bs.storage_code) AS warehouse_storage, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from eam_jig_material ejm left join eam_jig ej on ejm.jig_id=ej.jig_id left join base_warehouse bw on ej.warehouse_id=bw.warehouse_id left join base_warehouse_area bwa on ej.warehouse_area_id=bwa.warehouse_area_id left join base_storage bs on ej.storage_id=bs.storage_id left join sys_user u on ejm.create_user_id=u.user_id left join sys_user s on ejm.modified_user_id=s.user_id left join base_organization bo ON ejm.org_id = bo.organization_id
and ejm.jig_id = #{jigId}
and ej.jig_code like CONCAT('%', #{jigCode}, '%')
and ej.jig_name like CONCAT('%', #{jigName}, '%')
and ej.jig_desc like CONCAT('%', #{jigDesc}, '%')
and ej.jig_model like CONCAT('%', #{jigModel}, '%')
and date_format(ejm.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ejm.status = #{status}
and ejm.org_id=#{orgId}
and #FULL_FROM_QUERY
order by ejm.create_time desc

select , ej.jig_code,ej.jig_name,ej.jig_desc,ej.jig_model,ejml.usage_qty, CONCAT_WS(';',bw.warehouse_name,bwa.warehouse_area_name,bs.storage_code) AS warehouse_storage, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from eam_jig_material ejm LEFT JOIN eam_jig_material_list ejml ON ejm.jig_material_id = ejml.jig_material_id left join eam_jig ej on ejm.jig_id=ej.jig_id left join base_warehouse bw on ej.warehouse_id=bw.warehouse_id left join base_warehouse_area bwa on ej.warehouse_area_id=bwa.warehouse_area_id left join base_storage bs on ej.storage_id=bs.storage_id left join sys_user u on ejm.create_user_id=u.user_id left join sys_user s on ejm.modified_user_id=s.user_id left join base_organization bo ON ejm.org_id = bo.organization_id
and ejml.material_id = #{materialId}
and ejm.jig_id = #{jigId}
and ejm.org_id=#{orgId}
order by ejm.create_time desc
