mapper.select:
select , ejb.jig_barcode,ej.jig_code,ej.jig_name,ejc.jig_category_name, ejpip.jig_point_inspection_project_code,ejpip.jig_point_inspection_project_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from eam_jig_point_inspection_order ejpio left join eam_jig_point_inspection_project ejpip on ejpio.jig_point_inspection_project_id=ejpip.jig_point_inspection_project_id left join eam_jig ej on ejpio.jig_id=ej.jig_id left join eam_jig_category ejc on ej.jig_category_id=ejc.jig_category_id left join eam_jig_barcode ejb on ejpio.jig_barcode_id=ejb.jig_barcode_id left join sys_user u on ejpio.create_user_id=u.user_id left join sys_user s on ejpio.modified_user_id=s.user_id left join base_organization bo ON ejpio.org_id = bo.organization_id
and ejpio.jig_point_inspection_order_code like CONCAT('%', #{jigPointInspectionOrderCode}, '%')
and ejb.jig_barcode like CONCAT('%', #{jigBarcode}, '%')
and ej.jig_code like CONCAT('%', #{jigCode}, '%')
and ej.jig_name like CONCAT('%', #{jigName}, '%')
and ejc.jig_category_name like CONCAT('%', #{jigCategoryName}, '%')
and ejpio.order_status = #{orderStatus}
and ejpio.jig_barcode_id = #{jigBarcodeId}
and date_format(ejpio.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ejpio.status = #{status}
and ejpio.org_id=#{orgId}
and #FULL_FROM_QUERY
order by ejpio.create_time desc
