mapper.select:
select , ej.jig_code,ej.jig_name,ej.jig_desc,ej.jig_model, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_jig_re_material ejrm left join fantech_imes_v2.eam_jig ej on ejrm.jig_id=ej.jig_id left join ocean_v2.sys_user u on ejrm.create_user_id=u.user_id left join ocean_v2.sys_user s on ejrm.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ejrm.org_id = bo.organization_id
and ej.jig_code like CONCAT('%', #{jigCode}, '%')
and ej.jig_name like CONCAT('%', #{jigName}, '%')
and ej.jig_desc like CONCAT('%', #{jigDesc}, '%')
and ej.jig_model like CONCAT('%', #{jigModel}, '%')
and date_format(ejrm.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ejrm.status = #{status}
and ejrm.org_id=#{orgId}
group by ejrm.jig_id order by ejrm.create_time desc

select , bm.material_code,bm.material_name,bm.material_desc,bm.material_version, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_jig_re_material ejrm left join fantech_imes_v2.base_material bm on ejrm.material_id=bm.material_id left join ocean_v2.sys_user u on ejrm.create_user_id=u.user_id left join ocean_v2.sys_user s on ejrm.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ejrm.org_id = bo.organization_id
and ejrm.jig_id = #{jigId}
order by ejrm.create_time desc
