mapper.select:
select , ej.jig_code,ej.jig_name,ej.jig_desc,ej.jig_model,ejc.jig_category_name, ejb.jig_barcode,wo.work_order_code,isnull(ejret.jig_return_id) as requisition_status, ejr.create_time as requisition_time,ejret.create_time as return_time,ejret.this_time_usage_time, bw.warehouse_name,bwa.warehouse_area_name,bs.storage_code,bwo.working_area_code, su.user_name as usage_user_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from eam_jig_requisition ejr left join eam_jig_return ejret on ejr.jig_requisition_id=ejret.jig_requisition_id left join eam_jig ej on ejr.jig_id=ej.jig_id left join base_warehouse bw on ej.warehouse_id=bw.warehouse_id left join base_warehouse_area bwa on ej.warehouse_area_id=bwa.warehouse_area_id left join base_working_area bwo on ej.working_area_id=bwo.working_area_id left join base_storage bs on ej.storage_id=bs.storage_id left join eam_jig_category ejc on ej.jig_category_id=ejc.jig_category_id left join eam_jig_barcode ejb on ejr.jig_barcode_id=ejb.jig_barcode_id left join mes_pm_work_order wo on ejr.work_order_id=wo.work_order_id left join sys_user su on ejr.usage_user_id=su.user_id left join sys_user u on ejr.create_user_id=u.user_id left join sys_user s on ejr.modified_user_id=s.user_id left join base_organization bo ON ejr.org_id = bo.organization_id
and isnull(ejret.jig_return_id)=#{requisitionStatus}
and ejret.jig_return_id IS NULL
and ejr.jig_barcode_id = #{jigBarcodeId}
and ejr.jig_id = #{jigId}
and ejr.work_order_id = #{workOrderId}
and wo.work_order_code like CONCAT('%', #{workOrderCode}, '%')
and ejb.jig_barcode like CONCAT('%', #{jigBarcode}, '%')
and ej.jig_code like CONCAT('%', #{jigCode}, '%')
and ej.jig_name like CONCAT('%', #{jigName}, '%')
and ej.jig_model like CONCAT('%', #{jigModel}, '%')
and ejc.jig_category_name like CONCAT('%', #{jigCategoryName}, '%')
and date_format(ejr.create_time, '%Y-%m-%d') = #{requisitionTime}
and date_format(ejret.create_time, '%Y-%m-%d') = #{returnTime}
and date_format(ejr.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ejr.status = #{status}
and ejr.org_id=#{orgId}
and #FULL_FROM_QUERY
order by ejr.create_time desc

select ejr.jig_id from eam_jig_requisition ejr


  and ejr.work_order_id = #{workOrderId}


group by ejr.jig_id
