mapper.select:
SELECT, mpwo.work_order_code as work_order_code,mpwo.work_order_status as work_order_status,bm.material_code as material_code, bpm.product_model_name as product_model_name,bpm.product_model_code as product_model_code,br.route_name as route_name, bf.factory_name as factory_name,bws.work_shop_name as work_shop_name,bpl.pro_line_name as pro_line_name FROM eam_wi_release ewr LEFT JOIN mes_pm_work_order mpwo ON ewr.work_order_id = mpwo.work_order_id LEFT JOIN base_material bm ON ewr.material_id = bm.material_id LEFT JOIN base_material_tab bmt ON ewr.material_id=bmt.material_id LEFT JOIN base_product_model bpm ON bmt.product_model_id=bpm.product_model_id LEFT JOIN base_route br ON ewr.route_id = br.route_id LEFT JOIN base_factory bf ON ewr.factory_id = bf.factory_id LEFT JOIN base_work_shop bws ON ewr.work_shop_id = bws.work_shop_id LEFT JOIN base_pro_line bpl ON ewr.pro_line_id = bpl.pro_line_id LEFT JOIN ocean_v2.sys_user su ON ewr.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON ewr.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON ewr.org_id = bo.organization_id
and ewr.org_id = #{orgId}
and ewr.wi_release_code = #{wiReleaseCode}
and mpwo.work_order_code = #{workOrderCode}
and bm.material_code = #{materialCode}
