mapper.select:
select ,
pe.equipment_tag_id as equipmentTagId, pets.electronic_tag_id as electronicTagId, pets.electronic_tag_lang_type as electronicTagLangType,
pea.equipment_area_id as equipmentAreaId, pea.equipment_tag_id as equipmentAreaTagId, pcm.queue_name as queueName
from ptl_job_order_det pjod
LEFT JOIN base_storage bs ON pjod.storage_id = bs.storage_id
LEFT JOIN base_warehouse bw ON bs.warehouse_id = bw.warehouse_id
LEFT JOIN base_warehouse_area bwa ON bs.warehouse_area_id = bwa.warehouse_area_id
LEFT JOIN ptl_electronic_tag_storage pets ON pets.storage_id = bs.storage_id
LEFT JOIN ptl_equipment pe ON pets.equipment_id = pe.equipment_id
LEFT JOIN ptl_client_manage pcm ON pe.client_id = pcm.client_id
LEFT JOIN ptl_equipment pea ON pets.equipment_area_id = pea.equipment_id


    and pjod.job_order_id = #{jobOrderId}


    and pjod.storage_id = #{storageId}


    and pjod.storage_code = #{storageCode}


    and pjod.material_code = #{materialCode}


    and pjod.material_name like CONCAT('%', #{materialName}, '%')


    and pjod.whole_or_scattered = #{wholeOrScattered}


    and pjod.job_status = #{jobStatus}


    and se.equipment_tag_id = #{electronicTagId}


    and pets.electronic_tag_id = #{electronicTagId}


    and pets.equipment_area_id = #{equipmentAreaId}



  group by pjod.job_order_det_id

order by job_order_det_id
