mapper.select:
select ,
tv.vehicle_code as vehicleCode,
suw.user_code as workerUserCode,
suc.user_code as createUserCode,
sum.user_code as modifiedUserCode,
bw.warehouse_code as warehouseCode,
bwa.warehouse_area_id as warehouseAreaId,
bwa.warehouse_area_code as warehouseAreaCode,
bwa.warehouse_area_name as warehouseAreaName
from ptl_job_order pjo
LEFT JOIN ocean_v2.sys_user suw ON pjo.worker_user_id = suw.user_id
LEFT JOIN ocean_v2.sys_user suc ON pjo.create_user_id = suc.user_id
LEFT JOIN ocean_v2.sys_user sum ON pjo.modified_user_id = sum.user_id
LEFT JOIN tem_vehicle tv ON pjo.vehicle_id = tv.vehicle_id
LEFT JOIN ptl_job_order_det pjod ON pjo.job_order_id = pjod.job_order_id
LEFT JOIN base_storage bs ON pjod.storage_id = bs.storage_id
LEFT JOIN base_warehouse_area bwa ON bs.warehouse_area_id = bwa.warehouse_area_id
LEFT JOIN base_warehouse bw ON bwa.warehouse_id = bw.warehouse_id

  pjo.status = 1

    and pjo.job_order_id = #{jobOrderId}


    and pjo.job_order_code = #{jobOrderCode}


    and pjo.related_order_code = #{relatedOrderCode}


    and pjo.despatch_order_code = #{despatchOrderCode}


    and pjo.warehouse_name like CONCAT('%', #{warehouseName}, '%')


    and pjo.worker_user_name like CONCAT('%', #{workerUserName}, '%')


    and pjo.order_status = #{orderStatus}


    and pjo.order_status  !=  #{notOrderStatus}


    and pjo.order_status in (2,4,5) and pjo.pick_back_status = 0


    and pjo.create_time between #{startTime} and #{endTime}


    and TO_DAYS(pjo.create_time) = TO_DAYS(NOW())


    and bw.warehouse_id = #{warehouseId}


    and bwa.warehouse_area_id = #{warehouseAreaId}


    and bwa.warehouse_area_id in

      #{item}



    and pjo.org_id=#{orgId}


    and pjo.option1 = #{option1}


group by pjo.job_order_id
order by FIELD(pjo.order_status,2,1,4,5,3,6), pjo.related_order_code
