mapper.select:
SELECT, sus.user_name, ss.storage_desc, ss.storage_name, swa.warehouse_area_name, sw.warehouse_name, sm.material_desc, sm.material_name, se.equipment_name, se.equipment_code, se.equipment_ip, se.equipment_port, se.equipment_type, scm.queue_name, bo.organization_name FROM smt_ht_electronic_tag_storage shets LEFT JOIN ocean.sys_user su ON shets.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON shets.modified_user_id = sus.user_id LEFT JOIN smt_equipment se ON shets.equipment_id = se.equipment_id LEFT JOIN smt_client_manage scm ON scm.client_id = se.client_id LEFT JOIN smt_storage ss ON shets.storage_id = ss.storage_id LEFT JOIN smt_material sm ON shets.material_id = sm.material_id LEFT JOIN smt_warehouse sw ON shets.warehouse_id = sw.warehouse_id LEFT JOIN smt_warehouse_area swa ON shets.warehouse_area_id = swa.warehouse_area_id LEFT JOIN fantech_imes.base_organization bo ON shets.organization_id = bo.organization_id
and date_format(shets.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and shets.storage_id =#{storageId}
and ss.storage_name like CONCAT('%', #{storageName}, '%')
and sw.warehouse_name like CONCAT('%', #{warehouseName}, '%')
and swa.warehouse_area_name like CONCAT('%', #{warehouseAreaName}, '%')
and se.equipment_name like CONCAT('%', #{equipmentName}, '%')
and se.equipment_Id = #{equipmentId}
and se.equipment_ip like CONCAT('%', #{equipmentIp}, '%')
and shets.electronic_tag_id like CONCAT('%', #{electronicTagId}, '%')
and shets.status=#{status}
order by shets.modified_time desc
