mapper.select:
select , bm.material_code,bm.material_name,bpm.product_model_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from esop_ht_issue ehi left join base_material bm on ehi.material_id=bm.material_id left join base_material_tab bmt on ehi.material_id=bmt.material_id left join base_product_model bpm on bmt.product_model_id=bpm.product_model_id left join sys_user u on ehi.create_user_id=u.user_id left join sys_user s on ehi.modified_user_id=s.user_id left join base_organization bo ON ehi.org_id = bo.organization_id
and ehi.issue_code like CONCAT('%', #{issueCode}, '%')
and ehi.issue_name like CONCAT('%', #{issueName}, '%')
and bm.material_code like CONCAT('%', #{materialCode}, '%')
and bm.material_name like CONCAT('%', #{materialName}, '%')
and bpm.product_model_name like CONCAT('%', #{productModelName}, '%')
and date_format(ehi.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehi.status = #{status}
and ehi.org_id=#{orgId}
order by ehi.create_time desc
