mapper.select:
SELECT,
bm.material_code as material_code,bm.material_name as material_name,bm.material_desc as material_desc,spm.product_model_code as product_model_code,
spm.product_model_name as product_model_name,spm.product_model_desc as product_model_desc,sp.process_name as process_name,sp.process_code as process_code,su.user_name AS create_user_name,
CONCAT(bt.length,',',bt.width,',',bt.height) as specifications,

    bpl.pro_name as pro_name,

sus.user_name AS modified_user_name,bo.organization_name AS organization_name
FROM esop_work_instruction ewi
LEFT JOIN base_material bm ON ewi.material_id = bm.material_id
LEFT JOIN base_material_tab bt ON bm.material_id = bt.material_id
LEFT JOIN base_product_model spm ON ewi.product_model_id = spm.product_model_id
LEFT JOIN base_process sp ON ewi.process_id = sp.process_id

    LEFT JOIN esop_wi_release_det ewrd  ON ewrd.work_instruction_id = ewi.work_instruction_id
    LEFT JOIN esop_wi_release ewr ON ewr.wi_release_id = ewrd.wi_release_id
    LEFT JOIN base_pro_line bpl ON bpl.pro_line_id = ewr.pro_line_id

LEFT JOIN ocean_v2.sys_user su ON ewi.create_user_id = su.user_id
LEFT JOIN ocean_v2.sys_user sus ON ewi.modified_user_id = sus.user_id
LEFT JOIN base_organization bo ON ewi.org_id = bo.organization_id


        and ewi.org_id = #{orgId}


        and ewi.work_instruction_name like CONCAT('%', #{workInstructionName}, '%')


        and ewi.work_instruction_code = #{workInstructionCode}


        and bm.material_code = #{materialCode}


        and ewi.material_id = #{materialId}


        and ewi.process_id = #{processId}


        and ewr.pro_line_id = #{proLineId}


        and ewrd.wi_release_det_seq_num = #{equipmentSeqNum}


        and ewi.work_instruction_id = #{workInstructionId}


        and sp.process_name = like CONCAT('%', #{processName}, '%')


        and ewi.work_instruction_seq_num = #{workInstructionSeqNum}



order by ewi.modified_time desc

SELECT ewi.work_instruction_seq_num as work_instruction_seq_nums,spm.product_model_code as product_model_code,
spm.product_model_name as product_model_name,spm.product_model_desc as product_model_desc
FROM esop_work_instruction ewi
LEFT JOIN base_material bm ON ewi.material_id = bm.material_id
LEFT JOIN base_material_tab bt ON bm.material_id = bt.material_id
LEFT JOIN base_product_model spm ON ewi.product_model_id = spm.product_model_id
LEFT JOIN base_process sp ON ewi.process_id = sp.process_id

    LEFT JOIN esop_wi_release_det ewrd  ON ewrd.work_instruction_id = ewi.work_instruction_id
    LEFT JOIN esop_wi_release ewr ON ewr.wi_release_id = ewrd.wi_release_id
    LEFT JOIN base_pro_line bpl ON bpl.pro_line_id = ewr.pro_line_id

LEFT JOIN ocean_v2.sys_user su ON ewi.create_user_id = su.user_id
LEFT JOIN ocean_v2.sys_user sus ON ewi.modified_user_id = sus.user_id
LEFT JOIN base_organization bo ON ewi.org_id = bo.organization_id


        and ewi.org_id = #{orgId}


        and ewi.work_instruction_name like CONCAT('%', #{workInstructionName}, '%')


        and ewi.work_instruction_code = #{workInstructionCode}


        and bm.material_code = #{materialCode}


        and ewi.material_id = #{materialId}


        and ewi.process_id = #{processId}


        and ewr.pro_line_id = #{proLineId}


        and ewrd.wi_release_det_seq_num = #{equipmentSeqNum}


        and ewi.work_instruction_id = #{workInstructionId}


        and sp.process_name = like CONCAT('%', #{processName}, '%')


        and ewi.work_instruction_seq_num = #{workInstructionSeqNum}


group by ewi.work_instruction_seq_num
order by ewi.modified_time desc
