mapper.select:
select ,ewec.warning_event_name,su.user_name AS create_user_name,sus.user_name AS modified_user_name,bo.organization_name
FROM ews_warning_event_execute_log eweel
LEFT JOIN ews_warning_event_config ewec on eweel.warning_event_config_id = ewec.warning_event_config_id
LEFT JOIN sys_user su ON eweel.create_user_id = su.user_id
LEFT JOIN sys_user sus ON eweel.modified_user_id = sus.user_id
LEFT JOIN base_organization bo ON eweel.org_id = bo.organization_id


and eweel.warning_event_execute_log_id = #{warningEventExecuteLogId}


and eweel.warning_event_config_id = #{warningEventConfigId}


and eweel.org_id = #{orgId}

SELECT eweep.warning_event_execute_push_log_id as 'warningEventExecutePushLogId', ewec.warning_event_id_code as 'warningEventIdCode', ewec.warning_event_name as 'warningEventName', ewec.warning_event_desc as 'warningEventDesc', ewec.calling_method as 'callingMethod', ewpc.notification_method as 'notificationMethod', eweel.execute_time as 'executeTime', eweel.modified_time as 'modifiedTime', eweel.execute_result as 'executeResult', eweep.message_push_result as 'messagePushResult', eweel.handle_result as 'handleResult', eweep.handle_time as 'handleTime', eweep.personnel_level as'personnelLevel', eweel.execute_parameter as 'executeParameter', ewpc.warning_push_config_id as 'warningPushConfigId' FROM ews_warning_event_execute_push_log eweep LEFT JOIN ews_warning_event_execute_log eweel ON eweep.warning_event_execute_log_id = eweel.warning_event_execute_log_id LEFT JOIN ews_warning_event_config ewec ON eweel.warning_event_config_id = ewec.warning_event_config_id LEFT JOIN ews_warning_push_config ewpc ON ewec.warning_event_config_id = ewpc.warning_event_config_id AND ewpc.personnel_level = eweep.personnel_level
and ewec.warning_event_id_code like CONCAT('%',#{warningEventIdCode},'%')
and ewec.warning_event_name like CONCAT('%',#{warningEventName},'%')
and ewpc.notification_method = #{notificationMethod}
and eweel.execute_result = #{executeResult}
and eweep.message_push_result = #{messagePushResult}
and eweel.handle_result = #{handleResult}
eweep.personnel_level = #{personnelLevel}
and date_format(eweep.handle_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
