mapper.select:
select , cab.product_model,cab.qty, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name from call_agv_agv_task_barcode caatb LEFT JOIN fantech_imes_v2.call_agv_barcode cab ON caatb.barcode_id = cab.barcode_id LEFT JOIN ocean_v2.sys_user su ON caatb.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON caatb.modified_user_id = sus.user_id LEFT JOIN fantech_imes_v2.base_organization bo ON caatb.org_id = bo.organization_id
and caatb.agv_task_id = #{agvTaskId}
and date_format(caatb.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and caatb.org_id = #{orgId}
order by caatb.create_time desc
