mapper.select:
select , sstp.task_point_name as start_task_point_name, estp.task_point_name as end_task_point_name, tv.vehicle_name, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name from call_agv_agv_task caat LEFT JOIN fantech_imes_v2.base_storage_task_point sstp ON caat.start_storage_task_point_id = sstp.storage_task_point_id LEFT JOIN fantech_imes_v2.base_storage_task_point estp ON caat.end_storage_task_point_id = estp.storage_task_point_id LEFT JOIN fantech_imes_v2.tem_vehicle tv ON caat.vehicle_id = tv.vehicle_id LEFT JOIN ocean_v2.sys_user su ON caat.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON caat.modified_user_id = sus.user_id LEFT JOIN fantech_imes_v2.base_organization bo ON caat.org_id = bo.organization_id
and caat.operate_type = #{operateType}
and caat.task_code like CONCAT('%', #{taskCode}, '%')
and caat.task_status = #{taskStatus}
and sstp.task_point_name like CONCAT('%', #{startTaskPointName}, '%')
and estp.task_point_name like CONCAT('%', #{endTaskPointName}, '%')
and tv.vehicle_name like CONCAT('%', #{vehicleName}, '%')
and date_format(caat.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and caat.org_id = #{orgId}
and caat.create_user_id = #{userId}
and date_format(caat.create_time, '%Y-%m-%d') = #{createTime}
order by caat.create_time desc
