mapper.select:
select 
FROM eng_contract_qty_order ecqo
LEFT JOIN eng_purchase_req_order epro ON ecqo.option3=epro.option3
LEFT JOIN base_material bm ON ecqo.material_code=bm.material_code
LEFT JOIN base_material_tab bmt ON bm.material_id=bmt.material_id
LEFT JOIN ocean_v2.sys_user u ON ecqo.create_user_id=u.user_id
LEFT JOIN ocean_v2.sys_user s ON ecqo.modified_user_id=s.user_id
LEFT JOIN fantech_imes_v2.base_supplier bs ON ecqo.supplier_id = bs.supplier_id
LEFT JOIN fantech_imes_v2.base_organization bo ON ecqo.org_id = bo.organization_id


    and ecqo.contract_qty_order_id in

      #{item}



    and epro.purchase_req_order_code like CONCAT('%', #{purchaseReqOrderCode}, '%')


    and epro.purchase_req_order_name like CONCAT('%', #{purchaseReqOrderName}, '%')


    and epro.material_grade like CONCAT('%', #{materialGrade}, '%')


    and epro.purchase_req_explain = #{purchaseReqExplain}


    and epro.purchase_explain = #{purchaseExplain}


    and ecqo.contract_code like CONCAT('%', #{contractCode}, '%')


    and ecqo.supplier_id = #{supplierId}


    and ecqo.profession_name like CONCAT('%', #{professionName}, '%')


    and ecqo.contract_code = #{contractCode}


    and ecqo.device_code like CONCAT('%', #{deviceCode}, '%')


    and ecqo.dominant_term_code like CONCAT('%', #{dominantTermCode}, '%')


    and ecqo.location_num like CONCAT('%', #{locationNum}, '%')


    and ecqo.material_code like CONCAT('%', #{materialCode}, '%')


    and ecqo.material_code = #{materialCode}


    and bm.material_name like CONCAT('%', #{materialName}, '%')


    and ecqo.material_purpose = #{materialPurpose}


    and ecqo.supplier_id = #{supplierId}


    and bs.supplier_name = #{supplierName}


    and ecqo.remark like CONCAT('%', #{remark}, '%')


    and ecqo.pur_qty = #{purQty}


    and date_format(ecqo.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}


    and ecqo.status = #{status}


    and ecqo.org_id=#{orgId}


    and epro.purchase_req_order_code is not null


order by ecqo.contract_qty_order_id desc
