mapper.select:
select ,bm.material_code,bm.material_name,bm.material_desc,osu.nick_name as operator_user_name
from eng_material_maintain_log emml
left join ocean_v2.sys_user osu on osu.user_id = emml.operator_user_id
left join eng_contract_qty_order ecqo on ecqo.contract_qty_order_id = emml.contract_qty_order_id
left join base_material bm on ecqo.material_code=bm.material_code


    and date_format(emml.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}


    and date_format(emml.create_time, '%Y-%m-%d') = #{createTime}


    and emml.contract_qty_order_id = #{contractQtyOrderId}


    and bm.material_code like CONCAT('%', #{materialCode}, '%')


    and bm.material_name like CONCAT('%',  #{materialName}, '%')


    and bm.material_desc like CONCAT('%', #{materialDesc}, '%')


    and emml.maintain_content like CONCAT('%',  #{maintainContent}, '%')


    and emml.operator_user_id = #{operatorUserId}


    and osu.nick_name like CONCAT('%', #{operatorUserName}, '%')


    and emml.org_id=#{orgId}


order by emml.create_time desc
