mapper.select:
select ,
bs.supplier_name as supplier_name, bs.supplier_code as supplier_code,bse.shipment_enterprise_name as shipment_enterprise_name,
u.user_name as create_user_name,
s.user_name as modified_user_name,bo.organization_name,su.user_name as 'ago_confirm_user_name',
(SELECT IFNULL(SUM(eposd.qty),0) FROM eng_packing_order_summary_det eposd left join eng_packing_order_summary spos on eposd.packing_order_summary_id=spos.packing_order_summary_id where spos.packing_order_id=spo.packing_order_id) as 'totalQty',
(SELECT IFNULL(SUM(eposd.receiving_qty),0) FROM eng_packing_order_summary_det eposd left join eng_packing_order_summary spos on eposd.packing_order_summary_id=spos.packing_order_summary_id where spos.packing_order_id=spo.packing_order_id) as 'totalReceivingQty',
(SELECT IFNULL(SUM(eposd.distribution_qty),0) FROM eng_packing_order_summary_det eposd left join eng_packing_order_summary spos on eposd.packing_order_summary_id=spos.packing_order_summary_id where spos.packing_order_id=spo.packing_order_id) as 'totalDistributionQty',
(SELECT IFNULL(SUM(eposd.putaway_qty),0) FROM eng_packing_order_summary_det eposd left join eng_packing_order_summary spos on eposd.packing_order_summary_id=spos.packing_order_summary_id where spos.packing_order_id=spo.packing_order_id) as 'totalPutawayQty'
from eng_packing_order spo
left join base_supplier bs on spo.supplier_id=bs.supplier_id
left join base_shipment_enterprise bse on spo.shipment_enterprise_id=bse.shipment_enterprise_id
LEFT JOIN ocean_v2.sys_user su on su.user_id = spo.ago_confirm_user_id
left join ocean_v2.sys_user u on spo.create_user_id=u.user_id
left join ocean_v2.sys_user s on spo.modified_user_id=s.user_id
left join fantech_imes_v2.base_organization bo ON spo.org_id = bo.organization_id


    and date_format(spo.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}


    and bs.supplier_name like CONCAT('%', #{supplierName}, '%')


    and bse.shipment_enterprise_name like CONCAT('%', #{shipmentEnterpriseName}, '%')


    and spo.packing_order_code like CONCAT('%',  #{packingOrderCode}, '%')


    and spo.despatch_batch like CONCAT('%', #{despatchBatch}, '%')


    and spo.order_status in

       #{item}



    and spo.audit_status = #{auditStatus}


    and date_format(spo.order_time, '%Y-%m-%d %H:%i') BETWEEN #{orderTimeStart} AND #{orderTimeEnd}


    and date_format(spo.leave_factory_time, '%Y-%m-%d %H:%i') BETWEEN #{leaveFactoryTimeStart} AND #{leaveFactoryTimeEnd}


    and date_format(spo.leave_port_time, '%Y-%m-%d %H:%i') BETWEEN #{leavePortTimeStart} AND #{leavePortTimeEnd}


    and date_format(spo.arrival_port_time, '%Y-%m-%d %H:%i') BETWEEN #{arrivalPortTimeStart} AND #{arrivalPortTimeEnd}


    and date_format(spo.arrival_time, '%Y-%m-%d %H:%i') BETWEEN #{arrivalTimeStart} AND #{arrivalTimeEnd}


    and spo.status = #{status}


    and spo.org_id=#{orgId}


    and spo.supplier_id=#{supplierId}


    and spo.shipment_enterprise_id=#{shipmentEnterpriseId}


order by spo.create_time desc
