mapper.select:
select ,
bm.material_name as material_name,bm.material_code as material_code,spo.packing_order_code as packing_order_code,
bsp.supplier_name as supplier_name,spo.despatch_batch as despatch_batchs,spos.contract_code as contract_code,
spos.purchase_req_order_code as purchase_req_order_code,spos.profession_name as profession_name,
u.user_name as create_user_name,cast(qty as decimal(18,3)) as exportQty,
s.user_name as modified_user_name,
bo.organization_name,
bs.storage_name as 'receivingStorageName',
bs.storage_code as 'receivingStorageCode',
bss.storage_code as 'putawayStorageCode',
bss.storage_name as 'putawayStorageName'
from eng_packing_order_summary_det sposd
LEFT JOIN eng_packing_order_summary spos ON sposd.packing_order_summary_id = spos.packing_order_summary_id
LEFT JOIN eng_packing_order spo ON spos.packing_order_id = spo.packing_order_id
LEFT JOIN base_supplier bsp ON spo.supplier_id = bsp.supplier_id
left join base_shipment_enterprise bse on spo.shipment_enterprise_id=bse.shipment_enterprise_id
left join base_material bm on sposd.material_id=bm.material_id
LEFT JOIN base_storage bs on sposd.receiving_storage_id = bs.storage_id
LEFT JOIn base_storage bss on sposd.putaway_storage_id = bss.storage_id
left join ocean_v2.sys_user u on sposd.create_user_id=u.user_id
left join ocean_v2.sys_user s on sposd.modified_user_id=s.user_id
left join fantech_imes_v2.base_organization bo ON sposd.org_id = bo.organization_id


    and date_format(sposd.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}


    and sposd.status = #{status}


    and sposd.packing_order_summary_id=#{packingOrderSummaryId}


    and sposd.org_id=#{orgId}


    and sposd.summary_det_status in

       #{item}



    and spo.packing_order_id=#{packingOrderId}


  and sposd.raw_material_code like CONCAT('%',#{rawMaterialCode},'%')


    and sposd.spec like CONCAT('%',#{spec},'%')


  and bm.material_code like CONCAT('%',#{materialCode},'%')


    and bm.material_name like CONCAT('%',#{materialName},'%')



    and date_format(spo.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}


    and bsp.supplier_name like CONCAT('%', #{supplierName}, '%')


    and bse.shipment_enterprise_name like CONCAT('%', #{shipmentEnterpriseName}, '%')


    and spo.packing_order_code like CONCAT('%',  #{packingOrderCode}, '%')


    and spo.despatch_batch like CONCAT('%', #{despatchBatch}, '%')


    and spo.order_status in

      #{item}



    and spo.audit_status = #{auditStatus}


    and date_format(spo.order_time, '%Y-%m-%d %H:%i') BETWEEN #{orderTimeStart} AND #{orderTimeEnd}


    and date_format(spo.leave_factory_time, '%Y-%m-%d %H:%i') BETWEEN #{leaveFactoryTimeStart} AND #{leaveFactoryTimeEnd}


    and date_format(spo.leave_port_time, '%Y-%m-%d %H:%i') BETWEEN #{leavePortTimeStart} AND #{leavePortTimeEnd}


    and date_format(spo.arrival_port_time, '%Y-%m-%d %H:%i') BETWEEN #{arrivalPortTimeStart} AND #{arrivalPortTimeEnd}


    and date_format(spo.arrival_time, '%Y-%m-%d %H:%i') BETWEEN #{arrivalTimeStart} AND #{arrivalTimeEnd}


    and spo.status = #{status}


    and spo.org_id=#{orgId}


    and spo.supplier_id=#{supplierId}


    and spo.shipment_enterprise_id=#{shipmentEnterpriseId}



order by sposd.create_time desc
