mapper.select:
SELECT wijod.job_order_det_id, ecqo.option1, ecqo.option2, ecqo.contract_code, IFNULL(epro.purchase_req_order_code,'') AS purchase_req_order_code, bm.material_code, ecqo.location_num, ecqo.dominant_term_code, ecqo.device_code, wijod.distribution_qty AS plan_qty, bsout.option1 AS old_torageId, bsin.option1 AS new_storageId, wijod.create_time, u.user_name AS create_user_name FROM wms_inner_job_order_det wijod JOIN wms_inner_inventory wii ON wijod.source_det_id=wii.inventory_id LEFT JOIN sys_user u ON wijod.create_user_id=u.user_id LEFT JOIN wms_inner_job_order wijo ON wijod.job_order_id= wijo.job_order_id LEFT JOIN base_material bm ON wijod.material_id= bm.material_id LEFT JOIN eng_contract_qty_order ecqo ON wii.contract_code=ecqo.contract_code AND bm.material_code=ecqo.material_code LEFT JOIN eng_purchase_req_order epro ON ecqo.option3=epro.option3 LEFT JOIN base_storage bsout ON wijod.out_storage_id=bsout.storage_id LEFT JOIN base_storage bsin ON wijod.in_storage_id=bsin.storage_id
and wijo.job_order_id = #{jobOrderId}
GROUP BY wijod.job_order_det_id, ecqo.option1, ecqo.option2, ecqo.contract_code, epro.purchase_req_order_code, bm.material_code, ecqo.location_num, ecqo.dominant_term_code, ecqo.device_code, plan_qty, old_torageId, new_storageId, wijod.create_time, create_user_name
