mapper.select:
select wisod.stock_order_det_id, IFNULL(ecqo.option1,'') AS option1, IFNULL(ecqo.option2,'') AS option2, bs.option1 as DHGUID, IFNULL(wiisss.contract_code,'') as contract_code, IFNULL(wiisss.purchase_req_order_code,'') as purchase_req_order_code, bm.material_code, IFNULL(ecqo.location_num,'') AS location_num, IFNULL(wiisss.option2,'') AS dominant_term_code, IFNULL(wiisss.option1,'') AS device_code, ABS(wisod.variance_qty) AS variance_qty, CASE WHEN wisod.variance_qty>0 THEN '多余' ELSE '丢失' END AS inventory_status_name, wisod.create_time, u.user_name as create_user_name FROM wms_inner_stock_order_det wisod left join sys_user u on wisod.create_user_id=u.user_id left join wms_inner_stock_order wiso on wisod.stock_order_id= wiso.stock_order_id LEFT JOIN wms_inner_inventory wiisss ON wisod.storage_id= wiisss.storage_id AND wisod.material_id=wiisss.material_id AND IFNULL(wisod.batch_code,'')= IFNULL(wiisss.batch_code,'') left join base_inventory_status iis on wiisss.inventory_status_id= iis.inventory_status_id left join base_material bm on wisod.material_id= bm.material_id left join base_storage bs on wisod.storage_id= bs.storage_id left join eng_contract_qty_order ecqo on wiisss.contract_code=ecqo.contract_code and bm.material_code = ecqo.material_code
and wisod.stock_order_id = #{stockOrderId}
and wiisss.job_status =1
