mapper.select:
select , ra.area_code,ra.area_name, rbs.base_station_code,rbs.base_station_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.rfid_base_station_log rbsl left join fantech_imes_v2.rfid_area ra ON rbsl.area_id = ra.area_id left join fantech_imes_v2.rfid_base_station rbs ON rbsl.base_station_id = rbs.base_station_id left join ocean_v2.sys_user u on rbsl.create_user_id=u.user_id left join ocean_v2.sys_user s on rbsl.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON rbsl.org_id = bo.organization_id
and ra.area_name like CONCAT('%', #{areaName}, '%')
and ra.area_code like CONCAT('%', #{areaCode}, '%')
and rbs.base_station_code like CONCAT('%', #{baseStationCode}, '%')
and rbs.base_station_name like CONCAT('%', #{baseStationName}, '%')
and rbsl.normal_info like CONCAT('%', #{normalInfo}, '%')
and rbsl.abnormal_info like CONCAT('%', #{abnormalInfo}, '%')
and rbsl.if_normal = #{ifNormal}
and rbsl.status = #{status}
and date_format(rbsl.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and rbsl.org_id=#{orgId}
order by rbsl.create_time desc
