mapper.select:
select , bm.material_code,bm.material_desc,bm.material_version, bs.supplier_name, bis.inspection_standard_name, biw.inspection_way_code, bd.dept_name,su.user_name as audit_user_name, sus.user_name as inspection_user_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.qms_ht_inspection_order qhio left join fantech_imes_v2.base_material bm on qhio.material_id=bm.material_id left join fantech_imes_v2.base_supplier bs on qhio.customer_id=bs.supplier_id left join fantech_imes_v2.base_inspection_standard bis on qhio.inspection_standard_id=bis.inspection_standard_id left join fantech_imes_v2.base_inspection_way biw on qhio.inspection_way_id=biw.inspection_way_id left join fantech_imes_v2.base_dept bd on qhio.audit_dept_id=bd.dept_id left join ocean_v2.sys_user sus on qhio.inspection_user_id=sus.user_id left join ocean_v2.sys_user su on qhio.audit_user_id=su.user_id left join ocean_v2.sys_user u on qhio.create_user_id=u.user_id left join ocean_v2.sys_user s on qhio.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON qhio.org_id = bo.organization_id
and qhio.inspection_order_code like CONCAT('%', #{inspectionOrderCode}, '%')
and bm.material_code like CONCAT('%', #{materialCode}, '%')
and bs.supplier_name like CONCAT('%', #{customerName}, '%')
and qhio.sales_code like CONCAT('%', #{salesCode}, '%')
and qhio.same_package_code like CONCAT('%', #{samePackageCode}, '%')
and su.user_name like CONCAT('%', #{auditUserName}, '%')
and sus.user_name like CONCAT('%', #{inspectionUserName}, '%')
and qhio.inspection_status = #{inspectionStatus}
and qhio.inspection_result = #{inspectionResult}
and qhio.status = #{status}
and date_format(qhio.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and qhio.org_id=#{orgId}
order by qhio.create_time desc
