select , bm.material_code,bm.material_desc,bm.material_version, bs.supplier_name as customer_name, bis.inspection_standard_name, biw.inspection_way_code, biw.inspection_way_desc, bd.dept_name,su.user_name as audit_user_name, sus.user_name as inspection_user_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.qms_inspection_order qio left join fantech_imes_v2.base_material bm on qio.material_id=bm.material_id left join fantech_imes_v2.base_supplier bs on qio.customer_id=bs.supplier_id left join fantech_imes_v2.base_inspection_standard bis on qio.inspection_standard_id=bis.inspection_standard_id left join fantech_imes_v2.base_inspection_way biw on qio.inspection_way_id=biw.inspection_way_id left join fantech_imes_v2.base_dept bd on qio.audit_dept_id=bd.dept_id left join ocean_v2.sys_user sus on qio.inspection_user_id=sus.user_id left join ocean_v2.sys_user su on qio.audit_user_id=su.user_id left join ocean_v2.sys_user u on qio.create_user_id=u.user_id left join ocean_v2.sys_user s on qio.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON qio.org_id = bo.organization_id
and qio.inspection_order_id = #{inspectionOrderId}
and qio.same_package_code is null and qio.sales_code is null
and qio.inspection_order_code like CONCAT('%', #{inspectionOrderCode}, '%')
and bm.material_code like CONCAT('%', #{materialCode}, '%')
and bs.supplier_name like CONCAT('%', #{customerName}, '%')
and qio.sales_code like CONCAT('%', #{salesCode}, '%')
and qio.same_package_code like CONCAT('%', #{samePackageCode}, '%')
and su.user_name like CONCAT('%', #{auditUserName}, '%')
and sus.user_name like CONCAT('%', #{inspectionUserName}, '%')
and qio.inspection_status = #{inspectionStatus}
and qio.inspection_status
#{item}
and qio.inspection_result = #{inspectionResult}
and qio.status = #{status}
and date_format(qio.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and qio.org_id=#{orgId}
order by qio.create_time desc

select wiid.inventory_det_id, wiid.storage_id, wiid.material_id, wiid.barcode, wiid.material_qty, wiid.production_date, wiid.production_batch_code, wiid.supplier_id, wiid.job_status, wiid.if_stock_lock, wiid.lock_status, wiid.receiving_date, wiid.asn_code, wiid.deliver_date, wiid.delivery_order_code, wiid.barcode_status, wiid.status, wiid.remark, wiid.org_id, wiid.create_user_id, wiid.create_time, wiid.modified_user_id, wiid.modified_time, wiid.is_delete, wiid.contract_code, wiid.spec, wiid.purchase_req_order_code, wiid.carton_code, wiid.inventory_status_id, wiid.inspection_order_code, wiid.qc_date, wiid.option1, wiid.option2, wiid.option3, wiid.option4, wiid.option5, wiid.sales_barcode, wiid.customer_barcode, bs.storage_code,bs.warehouse_id,bw.warehouse_name,bm.material_code,bm.material_name,bss.supplier_name,bo.organization_name,bis.inventory_status_name, su.user_name AS create_user_name, sus.user_name AS modified_user_name from wms_inner_inventory_det wiid LEFT JOIN base_inventory_status bis ON bis.inventory_status_id = wiid.inventory_status_id LEFT JOIN base_storage bs ON wiid.storage_id = bs.storage_id LEFT JOIN wanbao_erp_logic wel ON bs.logic_id = wel.logic_id LEFT JOIN base_warehouse bw ON bs.warehouse_id = bw.warehouse_id LEFT JOIN base_material bm ON wiid.material_id = bm.material_id LEFT JOIN base_supplier bss ON wiid.supplier_id = bss.supplier_id LEFT JOIN ocean_v2.sys_user su ON wiid.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON wiid.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON wiid.org_id = bo.organization_id
and bw.warehouse_name like CONCAT('%',#{warehouseName},'%')
and wiid.storage_id = #{storageId}
and wiid.storage_id != #{storageId}
and wel.logic_code = #{logicCode}
and wel.logic_code != #{logicCode}
and wiid.inventory_status_id = #{inventoryStatusId}
and wiid.inspection_order_code like CONCAT('%',#{inspectionOrderCode},'%')
and (wiid.inspection_order_code is null or wiid.inspection_order_code = '')
and wiid.material_id = #{materialId}
#{value}
and wiid.barcode = #{barcode}
and wiid.sales_barcode like CONCAT('%',#{salesBarcode},'%')
and wiid.customer_barcode like CONCAT('%',#{customerBarcode},'%')
and bs.storage_code like CONCAT('%',#{storageCode},'%')
and wiid.material_qty = #{materialQty}
and bm.material_code like CONCAT('%',#{materialCode},'%')
and bm.material_name like CONCAT('%',#{materialName},'%')
and wiid.job_status = #{jobStatus}
and wiid.if_stock_lock = #{ifStockLock}
and wiid.lock_status = #{lockStatus}
and wiid.asn_code like CONCAT('%',#{asnCode},'%')
and bss.supplier_name like CONCAT('%',#{supplierName},'%')
and wiid.production_batch_code like CONCAT('%',#{productionBatchCode},'%')
and su.user_name like CONCAT('%',#{createUserName},'%')
and sus.user_name like CONCAT('%',#{modifiedUserName},'%')
and wiid.barcode_status = #{barcodeStatus}
and date_format(wiid.qc_date, '%Y-%m-%d')=#{qcDate}
and wiid.delivery_order_code like CONCAT('%',#{deliveryOrderCode},'%')
and wiid.org_id = #{orgId}
and wiid.option1 like CONCAT('%',#{option1},'%')
and wiid.option2 like CONCAT('%',#{option2},'%')
and wiid.option3 like CONCAT('%',#{option3},'%')
and wiid.option4 like CONCAT('%',#{option4},'%')
and wiid.option5 like CONCAT('%',#{option5},'%')
and bs.storage_type = #{storageType}
order by wiid.create_time desc
