mapper.select:
select shwob.ht_work_order_bom_id,shwob.work_order_bom_id,shwob.work_order_id,swo.work_order_code,shwob.part_material_id,sm.material_code as part_material_code, sm.material_name,sm.version,sm.material_desc,shwob.sub_material_id,sml.material_code as sub_material_code,shwob.process_id,sp.process_name,shwob.single_quantity, shwob.quantity,shwob.base_quantity,shwob.position,shwob.create_user_id, u.user_name as create_user_name,shwob.create_time,shwob.modified_user_id,s.user_name as modified_user_name, shwob.modified_time, shwob.is_delete from fantech_imes.smt_ht_work_order_bom shwob left join fantech_imes.smt_work_order swo on shwob.work_order_id=swo.work_order_id left join fantech_imes.smt_material sm on shwob.part_material_id=sm.material_id left join fantech_imes.smt_material sml on shwob.sub_material_id=sml.material_id left join fantech_imes.smt_process sp on shwob.process_id=sp.process_id left join ocean.sys_user u on shwob.create_user_id=u.user_id left join ocean.sys_user s on shwob.modified_user_id=s.user_id
1=1
and date_format(shwob.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and shwob.work_order_id=#{workOrderId}
and shwob.part_material_id=#{partMaterialId}
and sm.material_code like CONCAT('%', #{partMaterialCode}, '%')
and sm.material_desc like CONCAT('%', #{materialDesc}, '%')
order by shwob.modified_time desc
