mapper.select:
SELECT, su.user_name AS create_user_name, sus.user_name AS modified_user_name, spm.product_model_code AS product_model_code, sm1.material_code AS material_code, sm1.material_desc AS material_desc, sm1.version AS version, sm2.material_code AS part_material_code, sm2.material_desc AS part_material_desc, sm2.version AS part_material_version, sp.process_name AS process_name, ss.station_name AS station_name, bo.organization_name FROM fantech_imes.smt_ht_key_material shkm LEFT JOIN ocean.sys_user su ON shkm.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON shkm.modified_user_id = sus.user_id LEFT JOIN fantech_imes.smt_product_model spm ON shkm.product_model_id = spm.product_model_id LEFT JOIN fantech_imes.smt_material sm1 ON shkm.material_id = sm1.material_id LEFT JOIN fantech_imes.smt_material sm2 ON shkm.part_material_id = sm2.material_id LEFT JOIN fantech_imes.smt_process sp ON shkm.process_id = sp.process_id LEFT JOIN fantech_imes.smt_station ss ON shkm.station_id = ss.station_id LEFT JOIN fantech_imes.base_organization bo ON shkm.organization_id = bo.organization_id
and date_format(shkm.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and shkm.product_type = #{productType}
and spm.product_model_code like CONCAT('%', #{productModelCode}, '%')
and sp.process_name like CONCAT('%', #{processName}, '%')
and ss.station_name like CONCAT('%', #{stationName}, '%')
and shkm.status=#{status}
order by shkm.modified_time desc
