mapper.select:
select sp.process_id,sp.process_code,sp.process_name,sp.process_desc,sp.section_id,sws.section_name as sectionName,sp.process_category_id, spc.process_category_name as processCategoryName,sp.status,sp.create_user_id,u.user_name as createUserName,sp.create_time,sp.modified_user_id, s.user_name as modifiedUserName,sp.modified_time, sp.is_delete, sp.organization_id, sp.remark, bo.organization_name from fantech_imes.smt_process sp left join fantech_imes.smt_workshop_section sws on sp.section_id=sws.section_id left join smt_process_category spc on sp.process_category_id=spc.process_category_id left join ocean.sys_user u on sp.create_user_id=u.user_id left join ocean.sys_user s on sp.modified_user_id=s.user_id LEFT JOIN fantech_imes.base_organization bo ON sp.organization_id = bo.organization_id
1=1
and date_format(sp.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and sp.process_code like CONCAT('%', #{processCode}, '%')
and sp.process_name like CONCAT('%', #{processName}, '%')
and sp.process_desc like CONCAT('%', #{processDesc}, '%')
and sp.section_id=#{sectionId}
and sp.process_category_id=#{processCategoryId}
and sp.status=#{status}
order by sp.create_time desc
