mapper.select:
select , sm.material_code as part_material_code,sm.material_name,sm.version,sm.material_desc, sml.material_code as sub_material_code, sp.process_name, u.user_name as create_user_name,s.user_name as modified_user_name, bo.organization_name from fantech_imes.smt_product_bom_det spbd left join fantech_imes.smt_material sm on spbd.part_material_id=sm.material_id left join fantech_imes.smt_material sml on spbd.sub_material_id=sml.material_id left join fantech_imes.smt_process sp on spbd.process_id=sp.process_id left join ocean.sys_user u on spbd.create_user_id=u.user_id left join ocean.sys_user s on spbd.modified_user_id=s.user_id LEFT JOIN fantech_imes.base_organization bo ON spbd.organization_id = bo.organization_id
1=1
and date_format(spbd.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and spbd.product_bom_id=#{productBomId}
and spbd.part_material_id=#{materialId}
and sm.material_code like CONCAT('%', #{partMaterialCode}, '%')
and sm.material_desc like CONCAT('%', #{materialDesc}, '%')
and sm.parent_id = #{productBomDetId}
order by spbd.create_time desc
