mapper.select:
select , sw.warehouse_name as warehouseName, sw.warehouse_code, sw.warehouse_name, sw.warehouse_category, u.user_name as createUserName, s.user_name as modifiedUserName, swa.warehouse_area_code, swa.warehouse_area_name, swa.warehouse_area_name as warehouseAreaName, bo.organization_name from fantech_imes.smt_storage ss left join fantech_imes.smt_warehouse sw on ss.warehouse_id=sw.warehouse_id left join fantech_imes.smt_warehouse_area swa on ss.warehouse_area_id=swa.warehouse_area_id left join ocean.sys_user u on ss.create_user_id=u.user_id left join ocean.sys_user s on ss.modified_user_id=s.user_id LEFT JOIN fantech_imes.base_organization bo ON ss.organization_id = bo.organization_id
and date_format(ss.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ss.storage_id = #{storageId}
and ss.storage_code = #{storageCode}
and ss.storage_code like CONCAT('%', #{storageCode}, '%')
and ss.storage_name like CONCAT('%', #{storageName}, '%')
and ss.storage_desc like CONCAT('%', #{storageDesc}, '%')
and ss.warehouse_id=#{warehouseId}
and sw.warehouse_name like CONCAT('%', #{warehouseName}, '%')
and ss.warehouse_area_Id=#{warehouseAreaId}
and swa.warehouse_area_name like CONCAT('%', #{warehouseAreaName}, '%')
and ss.status=#{status}
order by ss.create_time desc

update.foreach:
update smt_storage
storage_code =#{item.storageCode}, storage_name =#{item.storageName}, `level` =#{item.level}, storage_desc =#{item.storageDesc}, capacity =#{capacity}, temperature =#{temperature}, unit =#{unit}, warehouse_state =#{warehouseState} warehouse_id =#{item.warehouseId}, warehouse_area_id =#{item.warehouseAreaId}, status =#{item.status}, create_user_id =#{item.createUserId}, create_time =#{item.createTime}, modified_user_id =#{item.modifiedUserId}, modified_time =#{item.modifiedTime}, is_delete =#{item.isDelete}, option1 =#{item.option1}, option2 =#{item.option2}, option3 =#{item.option3}
where storage_code =#{item.storageCode}
