mapper.select:
select sw.warehouse_id,sw.warehouse_code,sw.warehouse_name,sw.warehouse_desc,sw.warehouse_category, sw.capacity,sw.temperature,sw.unit,sw.status,sw.create_user_id, u.user_name as createUserName,sw.create_time,sw.modified_user_id, s.user_name as modifiedUserName, sw.modified_time, sw.is_delete, sw.organization_id, sw.remark, sw.complete_analysis, sw.mrb_operation, bo.organization_name from fantech_imes.smt_warehouse sw left join ocean.sys_user u on sw.create_user_id=u.user_id left join ocean.sys_user s on sw.modified_user_id=s.user_id LEFT JOIN fantech_imes.base_organization bo ON sw.organization_id = bo.organization_id
1=1
and date_format(sw.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and sw.warehouse_code = #{warehouseCode}
and sw.warehouse_code like CONCAT('%', #{warehouseCode}, '%')
and sw.warehouse_name like CONCAT('%', #{warehouseName}, '%')
and sw.warehouse_desc like CONCAT('%', #{warehouseDesc}, '%')
and sw.status=#{status}
order by sw.create_time desc

update.foreach:
update smt_warehouse
warehouse_code =#{item.warehouseCode}, warehouse_name =#{item.warehouseName}, warehouse_desc =#{item.warehouseDesc}, warehouse_category =#{warehouseCategory}, sw.capacity =#{capacity}, sw.temperature =#{temperature}, sw.unit =#{unit}, status =#{item.status}, create_user_id =#{item.createUserId}, create_time =#{item.createTime}, modified_user_id =#{item.modifiedUserId}, modified_time =#{item.modifiedTime}, is_delete =#{item.isDelete}, option1 =#{item.option1}, option2 =#{item.option2}, option3 =#{item.option3}
where warehouse_code =#{item.warehouseCode}
