mapper.select:
SELECT ,pb.work_order_id,wo.work_order_code,sm.material_code,sm.material_name,sm.version,sm.material_desc,wo.production_quantity,spl.pro_name,wo.work_order_status,sr.route_name,sp.process_id,sp.process_name,u.user_name as create_user_name,s.user_name as modified_user_name,bo.organization_name FROM mes_pm_break_bulk_det pbd left join mes_pm_break_bulk pb on pbd.break_bulk_id = pb.break_bulk_id LEFT JOIN smt_work_order wo ON pb.work_order_id = wo.work_order_id left join fantech_imes.smt_material sm on wo.material_id=sm.material_id LEFT JOIN base_tab bt on bt.material_id = sm.material_id left join smt_product_model spm on spm.product_model_id = bt.product_model_id LEFT join fantech_imes.smt_route sr on wo.route_id=sr.route_id LEFT join fantech_imes.smt_pro_line spl on wo.pro_line_id=spl.pro_line_id LEFT JOIN smt_process sp on sp.process_id = pb.process_id LEFT join ocean.sys_user u on pbd.create_user_id=u.user_id LEFT join ocean.sys_user s on pbd.modified_user_id=s.user_id LEFT JOIN fantech_imes.base_organization bo ON wo.organization_id = bo.organization_id
and pbd.break_bulk_id = #{breakBulkId}
and pb.batch_no = #{batchNo}
and pbd.child_lot_no = #{childLotNo}
and pbd.status=#{status}
