mapper.select:
select ,
mpwo.work_order_code,
oso.sales_order_code,
sm.material_code,
sm.material_name,
sm.material_desc,
sm.material_version,
mpwo.work_order_qty,
bpl.pro_name AS proName,
su.user_name AS create_user_name,
sus.user_name AS modified_user_name,
bo.organization_name AS organization_Name
from mes_pm_daily_plan_det mpdpd
LEFT JOIN mes_pm_daily_plan mpdp  ON mpdpd.daily_plan_id=mpdp.daily_plan_id
LEFT JOIN mes_pm_work_order mpwo ON mpdpd.work_order_id = mpwo.work_order_id
LEFT JOIN om_sales_order oso ON mpwo.sales_order_id = oso.sales_order_id
LEFT JOIN base_material sm on mpwo.material_id=sm.material_id
LEFT JOIN base_pro_line bpl ON  mpdp.pro_line_id =bpl.pro_line_id
LEFT JOIN sys_user su ON mpdp.create_user_id = su.user_id
LEFT JOIN sys_user sus ON mpdp.modified_user_id = sus.user_id
LEFT JOIN base_organization bo ON mpdp.org_id = bo.organization_id


    and mpdpd.daily_plan_det_id = #{dailyPlanDetId}


    and mpdpd.daily_plan_id = #{dailyPlanId}


    and mpdpd.core_source_order_code = #{coreSourceOrderCode}


    and mpdpd.source_order_code = #{sourceOrderCode}


    and mpdpd.core_source_id = #{coreSourceId}


    and mpdpd.source_id = #{sourceId}


    and mpdpd.work_order_id = #{workOrderId}


    and mpdpd.work_order_id = #{workOrderId}


    and mpdpd.remark = #{remark}


    and mpdpd.status = #{status}


    and mpdpd.if_order_inserting = #{ifOrderInserting}


    and date_format(mpdp.plan_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}


order by mpdpd.create_time desc
