mapper.select:
select ,
mpwo.material_id,mpwo.work_order_qty AS 'workOrderQty',mpwo.production_qty,mpwo.output_qty,
mpwo.work_order_status,mpwo.pro_line_id,mpwo.scheduled_qty,bm.material_code AS materialCode,
bm.material_version AS materialVersion,bm.material_desc AS materialDesc,bpl.pro_name AS proName,
date_format(mpdp.plan_time, '%Y-%m-%d') AS planDate,mpwo.work_order_code as workOrderCode,
bpl.pro_name,bm.material_name,(mpwo.work_order_qty-mpdp.schedule_qty) AS 'noScheduleQty'
from mes_pm_daily_plan mpdp
LEFT JOIN mes_pm_work_order mpwo ON mpdp.work_order_id = mpwo.work_order_id
LEFT JOIN base_pro_line bpl ON  mpwo.pro_line_id =bpl.pro_line_id
LEFT JOIN base_material bm ON  mpwo.material_id = bm.material_id


    and mpdp.daily_plan_id = #{dailyPlanId}


    and mpdp.work_order_id = #{workOrderId}


    and mpwo.material_id = #{materialId}


    and mpwo.pro_line_id = #{proLineId}


    and mpdp.status = #{status}


    and date_format(mpdp.plan_time, '%Y-%m-%d') = #{planTime}



order by mpdp.create_time desc
