mapper.select:
select , mpdp.daily_plan_code, mpdp.core_source_sys_order_type_code, mpwo.work_order_code, smp.material_code AS material_code_product, smp.material_name AS material_name_product, sm.material_code, sm.material_name, sm.material_desc, sm.material_version, mpwo.work_order_qty, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name AS organization_Name FROM mes_pm_daily_plan_stock_list mpdplist LEFT JOIN mes_pm_daily_plan_det mpdpd ON mpdplist.daily_plan_det_id=mpdpd.daily_plan_det_id LEFT JOIN mes_pm_daily_plan mpdp ON mpdpd.daily_plan_id=mpdp.daily_plan_id LEFT JOIN mes_pm_work_order mpwo ON mpdpd.work_order_id = mpwo.work_order_id LEFT JOIN base_material sm on mpdplist.part_material_id=sm.material_id LEFT JOIN base_material smp on mpwo.material_id=smp.material_id LEFT JOIN sys_user su ON mpdplist.create_user_id = su.user_id LEFT JOIN sys_user sus ON mpdplist.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON mpdplist.org_id = bo.organization_id
and mpdplist.daily_plan_stock_list_id = #{dailyPlanStockListId}
and mpdplist.daily_plan_det_id = #{dailyPlanDetId}
and mpdp.daily_plan_id = #{dailyPlanId}
and mpdplist.part_material_id = #{partMaterialId}
and mpdplist.process_id = #{processId}
and mpdplist.if_all_issued = #{ifAllIssued}
and mpdpd.remark = #{remark}
and mpdpd.status = #{status}
and date_format(mpdp.plan_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
order by mpdpd.create_time desc
