mapper.select:
SELECT mpepp.explain_process_plan_id, mpepp.explain_process_plan_code, mpepp.explain_plan_id, mpepp.process_id, mpepp.is_job_scan, mpepp.is_start_scan, mpepp.is_quality, mpepp.finished_qty, mpepp.planed_start_date, mpepp.planed_end_date, mpepp.actual_start_date, mpepp.actual_end_date, mpepp.organization_id, mpepp.remark, mpepp.is_delete, mpepp.create_user_id, mpepp.create_time, mpepp.modified_user_id, mpepp.modified_time, su1.user_name AS 'createUserName', su2.user_name AS 'modifiedUserName', bo.organization_name AS 'organizationName', sp.process_code AS 'processCode', sp.process_name AS 'processName' FROM fantech_imes_v2.mes_pm_explain_process_plan AS mpepp LEFT JOIN ocean_v2.sys_user su1 ON su1.user_id = mpepp.create_user_id LEFT JOIN ocean_v2.sys_user su2 ON su2.user_id = mpepp.modified_user_id LEFT JOIN fantech_imes_v2.base_organization bo on bo.organization_id = mpepp.organization_id LEFT JOIN fantech_imes_v2.mes_pm_explain_plan mpep on mpep.explain_plan_id = mpepp.explain_plan_id LEFT JOIN fantech_imes_v2.smt_work_order swo on swo.work_order_id = mpep.work_order_id LEFT JOIN fantech_imes_v2.smt_process sp on sp.process_id = mpepp.process_id
and date_format(mpepp.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and mpepp.explain_process_plan_code like CONCAT('%', #{explainProcessPlanCode}, '%')
and swo.work_order_code like CONCAT('%', #{workOrderCode}, '%')
and swo.material_id=#{materialId}
and mpepp.explain_plan_id=#{explainPlanId}
and mpepp.process_id=#{processId}
and swo.work_order_id=#{workOrderId}
and mpepp.is_delete = 1
order by mpepp.modified_time desc
