mapper.select:
SELECT mphwo.work_order_id, mphwo.work_order_code, mphwo.parent_id, mphwo.sales_order_id, mphwo.material_id, mphwo.work_order_qty, mphwo.production_qty, mphwo.output_qty, mphwo.scheduled_qty, mphwo.work_order_status, mphwo.pro_line_id, mphwo.barcode_rule_set_id, mphwo.route_id, mphwo.work_order_type, mphwo.schedule_date, mphwo.plan_start_time, mphwo.plan_end_time, mphwo.actual_start_time, mphwo.actual_end_time, mphwo.create_user_id, mphwo.create_time, mphwo.modified_user_id, mphwo.modified_time, mphwo.is_delete, mphwo.org_id, mphwo.put_into_process_id, mphwo.output_process_id, mphwo.scrap_qty, mphwo.inventory_qty, mphwo.remark, mphwo.contract_no, sm.material_code, sm.material_name, sm.material_version, sm.material_desc, bt.main_unit, bt.transfer_quantity AS 'transferQuantity', spl.pro_code, spl.pro_name, sr.route_name , sr.route_code, sbrs.barcode_rule_set_name AS 'barcodeRuleSetName', u.user_name AS 'createUserName', s.user_name AS 'modifiedUserName', bo.organization_name AS 'organizationName', bt.color AS 'color', spm.product_model_name AS 'productModelName', sps.package_specification_quantity AS 'packageSpecificationQuantity', putbp.process_name AS 'putIntoProcessName', outbp.process_name AS 'outputProcessName', oso.sales_order_code AS 'salesOrderCode' FROM mes_pm_ht_work_order mphwo left join base_material sm on mphwo.material_id=sm.material_id LEFT JOIN base_material_tab bt on bt.material_id = sm.material_id left join base_package_specification sps on sps.package_specification_id = bt.package_specification_id left join base_product_model spm on spm.product_model_id = bt.product_model_id left join base_route sr on mphwo.route_id=sr.route_id left join base_pro_line spl on mphwo.pro_line_id=spl.pro_line_id left join base_barcode_rule_set sbrs on mphwo.barcode_rule_set_id=sbrs.barcode_rule_set_id left join ocean_v2.sys_user u on mphwo.create_user_id=u.user_id left join ocean_v2.sys_user s on mphwo.modified_user_id=s.user_id LEFT JOIN base_organization bo ON mphwo.org_id = bo.organization_id left join base_process putbp ON putbp.process_id = mphwo.put_into_process_id left join base_process outbp ON outbp.process_id = mphwo.output_process_id left join om_sales_order oso ON oso.sales_order_id = mphwo.sales_order_id
and date_format(mphwo.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and mphwo.work_order_code like CONCAT('%', #{workOrderCode}, '%')
and mphwo.work_order_code = #{workOrderCode}
and mphwo.work_order_id =#{workOrderId}
and mphwo.parent_id like CONCAT('%', #{parentId}, '%')
and mphwo.material_id=#{materialId}
and mphwo.work_order_status =#{workOrderStatus}
and mphwo.work_order_status
#{item}
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and sm.material_desc like CONCAT('%', #{materialDesc}, '%')
and mphwo.is_delete = 1
order by mphwo.modified_time desc
