mapper.select:
SELECT wo.work_order_code AS 'workOrderCode', wo.production_quantity AS 'productionQuantity', wo.work_order_quantity AS 'workOrderQuantity', swocp.task_code AS 'taskCode', swobp.task_code AS 'barcodeTaskCode', swocp.work_order_card_id AS 'workOrderCardId', wo.material_id AS 'materialId', sm.material_code AS 'materialCode', sm.material_name AS 'materialName', sm.version AS 'version', wo.route_id AS 'routeId', sr.route_name AS 'routeName', sml.pro_name AS 'proName', mpplpr.process_list_process_re_id, mpplpr.process_list_process_re_code, mpplpr.work_order_card_pool_id, mpplpr.work_order_barcode_pool_id, mpplpr.process_id, sp.process_name AS 'processName', mpplpr.re_process_id, sp1.process_name AS 'reProcessName', mpplpr.re_qty, mpplpr.pre_qty, mpplpr.`status`, mpplpr.organization_id, mpplpr.remark, mpplpr.is_delete, mpplpr.create_user_id, mpplpr.create_time, mpplpr.modified_user_id, mpplpr.modified_time, splp.output_quantity AS 'outputQuantity' FROM fantech_imes.mes_pm_process_list_process_re mpplpr LEFT JOIN fantech_imes.smt_work_order_card_pool swocp ON swocp.work_order_card_pool_id = mpplpr.work_order_card_pool_id LEFT JOIN fantech_imes.smt_work_order_barcode_pool swobp ON swobp.work_order_barcode_pool_id = mpplpr.work_order_barcode_pool_id LEFT JOIN fantech_imes.smt_work_order wo ON wo.work_order_id = swocp.work_order_id LEFT JOIN fantech_imes.smt_material sm ON sm.material_id = wo.material_id LEFT JOIN fantech_imes.base_tab bt on bt.material_id = sm.material_id left join fantech_imes.smt_route sr on wo.route_id=sr.route_id left join fantech_imes.smt_order so on wo.order_id=so.order_id LEFT JOIN fantech_imes.smt_station ss ON ss.process_id = mpplpr.process_id LEFT JOIN fantech_imes.smt_pro_line sml ON wo.pro_line_id = sml.pro_line_id LEFT JOIN fantech_imes.smt_process sp ON sp.process_id = mpplpr.process_id LEFT JOIN fantech_imes.smt_process sp1 ON sp.process_id = mpplpr.re_process_id LEFT JOIN fantech_imes.smt_process_list_process splp ON splp.work_order_card_pool_id = swocp.work_order_card_pool_id AND splp.process_id = mpplpr.re_process_id LEFT JOIN fantech_imes.base_organization bo ON mpplpr.organization_id = bo.organization_id
and date_format(mpplpr.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and wo.work_order_code like CONCAT('%', #{workOrderCode}, '%')
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and sml.pro_line_id=#{proLineId}
and mpplpr.work_order_card_pool_id=#{workOrderCardPoolId}
and mpplpr.process_list_process_re_code=#{processListProcessReCode}
and mpplpr.process_id=#{processId}
and mpplpr.re_process_id=#{reProcessId}
and mpplpr.status=#{status}
and mpplpr.is_delete = 1
order by mpplpr.modified_time desc
