mapper.select:
SELECT swo.work_order_id, swo.work_order_code, swo.parent_id, swo.sales_order_id, swo.material_id, swo.work_order_qty, swo.production_qty, swo.output_qty, swo.scheduled_qty, swo.work_order_status, swo.pro_line_id, swo.barcode_rule_set_id, swo.route_id, swo.work_order_type, swo.schedule_date, swo.plan_start_time, swo.plan_end_time, swo.actual_start_time, swo.actual_end_time, swo.create_user_id, swo.create_time, swo.modified_user_id, swo.modified_time, swo.is_delete, swo.org_id, swo.put_into_process_id, swo.output_process_id, swo.scrap_qty, swo.inventory_qty, swo.remark, swo.contract_no, swo.batch_code, swo.logic_id, swo.option1, swo.option2, swo.option3, sm.material_code AS material_code, sm.material_name AS material_name, sm.material_version AS material_version, sm.material_desc AS material_desc, bt.main_unit AS 'mainUnit', bt.transfer_quantity AS 'transferQuantity', spl.pro_code AS 'proCode', spl.pro_name AS 'proName', sr.route_name AS 'routeName', sr.route_code AS 'routeCode', sbrs.barcode_rule_set_name AS 'barcodeRuleSetName', u.user_name AS 'createUserName', s.user_name AS 'modifiedUserName', bo.organization_name AS 'organizationName', bt.color AS 'color', spm.product_model_name AS 'productModelName', sps.package_specification_quantity AS 'packageSpecificationQuantity', putbp.process_name AS 'putIntoProcessName', outbp.process_name AS 'outputProcessName', oso.sales_order_code AS 'salesOrderCode', osod.sales_code AS 'salesCode', wel.logic_name FROM mes_pm_work_order swo left join base_material sm on swo.material_id=sm.material_id LEFT JOIN base_material_tab bt on bt.material_id = sm.material_id left join base_package_specification sps on sps.package_specification_id = bt.package_specification_id left join base_product_model spm on spm.product_model_id = bt.product_model_id left join base_route sr on swo.route_id=sr.route_id left join base_pro_line spl on swo.pro_line_id=spl.pro_line_id left join base_barcode_rule_set sbrs on swo.barcode_rule_set_id=sbrs.barcode_rule_set_id left join ocean_v2.sys_user u on swo.create_user_id=u.user_id left join ocean_v2.sys_user s on swo.modified_user_id=s.user_id LEFT JOIN base_organization bo ON swo.org_id = bo.organization_id left join base_process putbp ON putbp.process_id = swo.put_into_process_id left join base_process outbp ON outbp.process_id = swo.output_process_id left join om_sales_order oso ON oso.sales_order_id = swo.sales_order_id left join om_sales_order_det osod on osod.sales_order_id = oso.sales_order_id and osod.material_id = swo.material_id left join wanbao_erp_logic wel on wel.logic_id = swo.logic_id
and date_format(swo.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and swo.work_order_code like CONCAT('%', #{workOrderCode}, '%')
and swo.work_order_code = #{workOrderCode}
and swo.work_order_id =#{workOrderId}
and swo.parent_id like CONCAT('%', #{parentId}, '%')
and swo.material_id=#{materialId}
and swo.work_order_status =#{workOrderStatus}
and swo.option1 =#{option1}
and swo.option2 =#{option2}
and swo.option3 =#{option3}
and swo.work_order_status
#{item}
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and sm.material_desc like CONCAT('%', #{materialDesc}, '%')
and oso.sales_order_code like CONCAT('%', #{salesOrderCode}, '%')
and sr.route_name like CONCAT('%', #{routeName}, '%')
and spl.pro_name like CONCAT('%', #{proName}, '%')
and swo.pro_line_id=#{proLineId}
and swo.org_id =#{orgId}
and swo.is_delete = 1
GROUP BY swo.work_order_id order by swo.modified_time desc
