mapper.select:
select ,su.user_name as create_user_name,sus.user_name as modified_user_name,bo.organization_name, bp.process_code,bp.process_name,bp.process_desc,bws.section_name,bpc.process_category_name,mpwo.work_order_code, bm.material_code,bm.material_desc,bm.material_version from mes_pm_work_order_process_re_wo mpwoprw LEFT JOIN ocean_v2.sys_user su ON mpwoprw.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON mpwoprw.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON mpwoprw.org_id = bo.organization_id LEFT JOIN mes_pm_work_order mpwo ON mpwoprw.work_order_id = mpwo.work_order_id LEFT JOIN base_material bm ON mpwo.material_id = bm.material_id LEFT JOIN base_process bp ON bp.process_id = mpwoprw.process_id LEFT JOIN base_workshop_section bws ON bws.section_id = bp.section_id LEFT JOIN base_process_category bpc ON bpc.process_category_id = bp.process_category_id
and mpwoprw.work_order_id = #{workOrderId}
and mpwo.work_order_code like CONCAT('%', #{workOrderCode}, '%')
and bm.material_code like CONCAT('%', #{materialCode}, '%')
and bm.material_id like CONCAT('%', #{materialId}, '%')
and bm.material_desc like CONCAT('%', #{materialDesc}, '%')
and bp.process_id = #{processId}
and mpwoprw.status = #{status}
and date_format(mpwoprw.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
order by mpwoprw.create_time desc
