mapper.select:
select 
FROM mes_sfc_product_carton_det mspcd


    and mspcd.product_carton_id = #{productCartonId}


    and mspcd.work_order_barcode_id = #{workOrderBarcodeId}

SELECT mspcd.product_carton_id AS product_pallet_id, mswob.work_order_id AS source_order_id, mpwo.material_id AS material_id, sps.package_specification_quantity AS packing_unit_name, COUNT( mspcd.work_order_barcode_id ) AS packing_qty, COUNT( mspcd.work_order_barcode_id ) AS actual_qty, bs.supplier_name, oscrs.sales_code, oso.sales_user_name FROM mes_sfc_product_carton_det mspcd LEFT JOIN mes_sfc_work_order_barcode mswob ON mspcd.work_order_barcode_id = mswob.work_order_barcode_id LEFT JOIN mes_pm_work_order mpwo ON mswob.work_order_id = mpwo.work_order_id LEFT JOIN base_material_tab bt ON bt.material_id = mpwo.material_id LEFT JOIN base_package_specification sps ON sps.package_specification_id = bt.package_specification_id LEFT JOIN om_sales_order oso ON mpwo.sales_order_id = oso.sales_order_id LEFT JOIN base_supplier bs ON oso.supplier_id = bs.supplier_id LEFT JOIN mes_sfc_barcode_process msbp ON mswob.work_order_barcode_id = msbp.work_order_barcode_id LEFT JOIN om_sales_code_re_spc oscrs ON msbp.sales_code_re_spc_id = oscrs.sales_code_re_spc_id
and mspcd.org_id = #{orgId}
#{value}
GROUP BY mspcd.product_carton_id, mswob.work_order_id
