mapper.select:
select , mspc.carton_code as cartonCode
FROM mes_sfc_product_pallet_det msppd
LEFT JOIN mes_sfc_product_carton_det mspcd ON msppd.work_order_barcode_id = mspcd.work_order_barcode_id
LEFT JOIN mes_sfc_product_carton mspc ON mspcd.product_carton_id = mspc.product_carton_id


    and msppd.product_pallet_id = #{productPalletId}



      #{value}



    and msppd.work_order_barcode_id = #{workOrderBarcodeId}



  group by cartonCode

SELECT msppd.product_pallet_id AS product_pallet_id, mswob.work_order_id AS source_order_id, mpwo.material_id AS material_id, sps.package_specification_quantity AS packing_unit_name, COUNT( msppd.work_order_barcode_id ) AS packing_qty, COUNT( msppd.work_order_barcode_id ) AS actual_qty FROM mes_sfc_product_pallet_det msppd LEFT JOIN mes_sfc_work_order_barcode mswob ON msppd.work_order_barcode_id = mswob.work_order_barcode_id LEFT JOIN mes_pm_work_order mpwo ON mswob.work_order_id = mpwo.work_order_id LEFT JOIN base_material_tab bt ON bt.material_id = mpwo.material_id LEFT JOIN base_package_specification sps ON sps.package_specification_id = bt.package_specification_id
and msppd.org_id = #{orgId}
#{value}
GROUP BY msppd.product_pallet_id, mswob.work_order_id
