mapper.select:
select,su.user_name as create_user_name,sus.user_name as modified_user_name,bo.organization_name,blc.label_category_name,msbp.same_package_code, CASE mswob.option1 WHEN 5 THEN sod.material_id ELSE mpwo.material_id END 'material_id', CASE mswob.option1 WHEN 5 THEN bms.material_code ELSE bm.material_code END 'material_code', CASE mswob.option1 WHEN 5 THEN bms.material_name ELSE bm.material_name END 'material_name' FROM mes_sfc_work_order_barcode mswob LEFT JOIN ocean_v2.sys_user su ON mswob.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON mswob.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON mswob.org_id = bo.organization_id LEFT JOIN mes_pm_work_order mpwo ON mswob.work_order_id = mpwo.work_order_id left join om_sales_order_det sod on mswob.work_order_id = sod.sales_order_det_id LEFT JOIN base_material bm on mpwo.material_id = bm.material_id left join base_material bms on sod.material_id = bms.material_id left join base_label_category blc on mswob.label_category_id = blc.label_category_id LEFT JOIN mes_sfc_barcode_process msbp ON mswob.work_order_barcode_id = msbp.work_order_barcode_id
and mswob.work_order_id = #{workOrderId}
and mswob.labelCategoryId = #{labelCategoryId}
and mswob.barcode = #{barcode}
and mswob.work_order_barcode_id = #{workOrderBarcodeId}
and mswob.org_id =#{orgId}
and mswob.barcode_status = #{barcodeStatus}
and blc.label_category_name = '工单流转卡'
and blc.label_category_name = '产品条码'
and blc.label_category_name = '客户条码'
and blc.label_category_name = '销售订单条码'
order by mswob.create_time desc

select barcode
FROM mes_sfc_work_order_barcode
where labelCategoryId = #{barcodeType}
  and work_order_id = #{workOrderId}
order by work_order_barcode_id desc LIMIT 1

select label_category_id
FROM base_label_category
where label_category_name = #{labelCategoryName}

select COUNT(*)
FROM mes_sfc_work_order_barcode
where label_category_id = #{barcodeType}
  and work_order_id = #{workOrderId}

select label_version
From base_label
where label_name = #{labelName}

SELECT
FROM mes_sfc_product_carton_det mspcd LEFT JOIN mes_sfc_work_order_barcode mswob ON mswob.work_order_barcode_id = mspcd.work_order_barcode_id LEFT JOIN mes_sfc_product_carton mspc ON mspc.product_carton_id = mspcd.product_carton_id
and mspcd.product_carton_id = #{productCartonId}
and mspc.carton_code = #{cartonCode}
GROUP BY mswob.work_order_id

SELECT mswob.work_order_id AS source_order_id, mpwo.material_id AS material_id, sps.package_specification_quantity AS packing_unit_name, COUNT( mswob.work_order_barcode_id ) AS packing_qty, COUNT( mswob.work_order_barcode_id ) AS actual_qty FROM mes_sfc_work_order_barcode mswob LEFT JOIN mes_pm_work_order mpwo ON mswob.work_order_id = mpwo.work_order_id LEFT JOIN base_material_tab bt ON bt.material_id = mpwo.material_id LEFT JOIN base_package_specification sps ON sps.package_specification_id = bt.package_specification_id
and mswob.org_id = #{orgId}
#{value}
GROUP BY mswob.work_order_id

SELECT , bm.material_code, msbp.same_package_code, mskpr.part_barcode AS 'cutsomer_barcode', mskpr2.part_barcode AS 'sales_barcode' FROM mes_sfc_work_order_barcode mswob LEFT JOIN mes_sfc_product_pallet_det msppd ON msppd.work_order_barcode_id = mswob.work_order_barcode_id LEFT JOIN mes_pm_work_order mpwo ON mswob.work_order_id = mpwo.work_order_id LEFT JOIN base_material bm ON mpwo.material_id = bm.material_id LEFT JOIN mes_sfc_barcode_process msbp ON mswob.work_order_barcode_id = msbp.work_order_barcode_id LEFT JOIN mes_sfc_key_part_relevance mskpr ON mswob.work_order_barcode_id = mskpr.work_order_barcode_id AND mskpr.material_id IS NOT NULL LEFT JOIN mes_sfc_key_part_relevance mskpr2 ON mswob.work_order_barcode_id = mskpr2.work_order_barcode_id AND mskpr2.label_category_id IS NOT NULL
and mswob.org_id = #{orgId}
and msppd.product_pallet_id = #{productPalletId}

SELECT , bm.material_code, msbp.same_package_code, mskpr.part_barcode AS 'cutsomer_barcode', mskpr2.part_barcode AS 'sales_barcode' FROM mes_sfc_work_order_barcode mswob LEFT JOIN mes_sfc_product_carton_det mspcd ON mspcd.work_order_barcode_id = mswob.work_order_barcode_id LEFT JOIN mes_pm_work_order mpwo ON mswob.work_order_id = mpwo.work_order_id LEFT JOIN base_material bm ON mpwo.material_id = bm.material_id LEFT JOIN mes_sfc_barcode_process msbp ON mswob.work_order_barcode_id = msbp.work_order_barcode_id LEFT JOIN mes_sfc_key_part_relevance mskpr ON mswob.work_order_barcode_id = mskpr.work_order_barcode_id AND mskpr.material_id IS NOT NULL LEFT JOIN mes_sfc_key_part_relevance mskpr2 ON mswob.work_order_barcode_id = mskpr2.work_order_barcode_id AND mskpr2.label_category_id IS NOT NULL
and mswob.org_id = #{orgId}
and mspcd.product_carton_id = #{productCartonId}
