mapper.select:
SELECT,bmo.material_owner_name,bw.warehouse_name as 'out_warehouse_name',bw1.warehouse_name as 'in_warehouse_name', su.user_name as create_user_name,sus.user_name as modified_user_name,bo.organization_name, (SELECT SUM(otod.order_qty) FROM om_transfer_order_det otod where otod.transfer_order_id = oto.transfer_order_id) as total_qty, (SELECT IFNULL(SUM(bmb.volume *otod.order_qty),0) FROM om_transfer_order_det otod left join base_material_tab bmb ON otod.material_id = bmb.material_id where otod.transfer_order_id = oto.transfer_order_id) as total_volume, (SELECT IFNULL(SUM(bmb.net_weight *otod.order_qty),0) FROM om_transfer_order_det otod left join base_material_tab bmb ON otod.material_id = bmb.material_id where otod.transfer_order_id = oto.transfer_order_id) as total_net_weight, (SELECT IFNULL(SUM(bmb.gross_weight * otod.order_qty),0) FROM om_transfer_order_det otod left join base_material_tab bmb ON otod.material_id = bmb.material_id where otod.transfer_order_id = oto.transfer_order_id) as total_gross_weight FROM om_ht_transfer_order oto LEFT JOIN base_material_owner bmo ON oto.material_owner_id = bmo.material_owner_id LEFT JOIN base_warehouse bw ON oto.out_warehouse_id = bw.warehouse_id LEFT JOIN base_warehouse bw1 ON oto.in_warehouse_id = bw1.warehouse_id left join sys_user su on oto.create_user_id = su.user_id left join sys_user sus on oto.modified_user_id = sus.user_id left join base_organization bo on oto.org_id = bo.organization_id
and oto.org_id = #{orgId}
and oto.transfer_order_code like CONCAT('%',#{transferOrderCode},'%')
and oto.related_order_code like CONCAT('%',#{relatedOrderCode},'%')
and bw.warehouse_name like CONCAT('%',#{outWarehouseName},'%')
and bw1.warehouse_name like CONCAT('%',#{inWarehouseName},'%')
and oto.order_status = #{orderStatus}
and date_format(oto.order_date, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
order BY oto.create_time desc
