mapper.select:
SELECT,su.user_name as create_user_name,sus.user_name as modified_user_name,bo.organization_name, (SELECT SUM(oiod.order_qty) FROM om_other_in_order_det oiod where oiod.other_in_order_id = oio.other_in_order_id) as total_qty, (SELECT SUM(oiod.issue_qty) FROM om_other_in_order_det oiod where oiod.other_in_order_id = oio.other_in_order_id) as total_issue_qty, (SELECT SUM(oiod.receiving_qty) FROM om_other_in_order_det oiod where oiod.other_in_order_id = oio.other_in_order_id) as total_receiving_qty, (SELECT IFNULL(SUM(bmb.volume*oiod.order_qty),0) FROM om_other_in_order_det oiod left join base_material_tab bmb ON oiod.material_id = bmb.material_id where oiod.other_in_order_id = oio.other_in_order_id) as total_volume, (SELECT IFNULL(SUM(bmb.net_weight*oiod.order_qty),0) FROM om_other_in_order_det oiod left join base_material_tab bmb ON oiod.material_id = bmb.material_id where oiod.other_in_order_id = oio.other_in_order_id) as total_net_weight, (SELECT IFNULL(SUM(bmb.gross_weight*oiod.order_qty),0) FROM om_other_in_order_det oiod left join base_material_tab bmb ON oiod.material_id = bmb.material_id where oiod.other_in_order_id = oio.other_in_order_id) as total_gross_weight FROM om_other_in_order oio left join sys_user su on oio.create_user_id = su.user_id left join sys_user sus on oio.modified_user_id = sus.user_id left join base_organization bo on oio.org_id = bo.organization_id
and oio.other_in_order_code like CONCAT('%',#{otherInOrderCode},'%')
and oio.related_order_code like CONCAT('%',#{relatedOrderCode},'%')
and oio.org_id = #{orgId}
and oio.order_status = #{orderStatus}
and date_format(oio.order_date, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
order by oio.create_time desc
