mapper.select:
select , (select IFNULL(sum(oprod.order_qty),0) FROM om_purchase_return_order_det oprod WHERE oprod.purchase_order_det_id = opod.purchase_order_det_id) as purchase_return_qty, bm.material_code as materialCode, bm.material_name as materialName, bm.material_desc as materialDesc, bm.material_version as materialVersion, bm.material_source as materialSource,bmc.material_category_name as materialCategoryName, bpm.product_model_name as productModelName,opo.purchase_order_code,opo.warehouse_id,bw.warehouse_name,bw.warehouse_code,opo.supplier_id, bs.supplier_name as supplierName,bmt.volume as volume, bmt.net_weight as netWeight,bmt.gross_weight as grossWeight, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from om_purchase_order_det opod left join om_purchase_order opo on opo.purchase_order_id = opod.purchase_order_id left join base_warehouse bw on opo.warehouse_id = bw.warehouse_id left join sys_user suc on opod.create_user_id = suc.user_id left join sys_user sumod on opod.modified_user_id = sumod.user_id left join base_material bm on opod.material_id = bm.material_id left join base_material_tab bmt on opod.material_id = bmt.material_id left join base_material_category bmc on bmt.material_category_id = bmc.material_category_id left join base_product_model bpm on bmt.product_model_id = bpm.product_model_id left join base_supplier bs on opo.supplier_id = bs.supplier_id left join sys_user u on opod.create_user_id=u.user_id left join sys_user s on opod.modified_user_id=s.user_id left join base_organization bo on opod.org_id = bo.organization_id
and opod.actual_qty > 0
and opo.supplier_id = #{supplierId}
and opod.status = #{status}
and opod.org_id = #{orgId}
and opod.purchase_order_id = #{purchaseOrderId}
and opod.purchase_order_det_id = #{purchaseOrderDetId}
and opo.purchase_order_code = #{purchaseOrderCode}
and bm.material_code = #{materialCode}
and opo.purchase_order_code like CONCAT('%', #{purchaseOrderCode}, '%')
and bm.material_code like CONCAT('%', #{materialCode}, '%')
order by opod.modified_time desc

update.foreach:
update om_purchase_order_det

    if_all_issued = IF ( ( total_issue_qty + #{item.totalIssueQty} ) = order_qty, 1, 0 ),
    total_issue_qty =  IFNULL(total_issue_qty,0)+#{item.totalIssueQty}

WHERE purchase_order_det_id = #{item.purchaseOrderDetId}
