mapper.select:
select , bs.supplier_name,bd.dept_name as sales_dept, susales.user_name as sales_user_name, sumake.user_name as make_order_user_name, suaudit.user_name as audit_user_name, bbrs.barcode_rule_set_name, suc.user_name as create_user_name, bo.organization_name, sumod.user_name as modified_user_name from om_sales_order so left join base_dept bd on so.sales_dept_id = bd.dept_id left join base_supplier bs on so.supplier_id = bs.supplier_id left join base_barcode_rule_set bbrs on so.barcode_rule_set_id = bbrs.barcode_rule_set_id left join sys_user suc on so.create_user_id = suc.user_id left join base_organization bo on so.org_id = bo.organization_id left join sys_user sumod on so.modified_user_id = sumod.user_id left join sys_user susales on so.sales_user_id = susales.user_id left join sys_user sumake on so.make_order_user_id = sumake.user_id left join sys_user suaudit on so.audit_user_id = suaudit.user_id
left join mes_pm_work_order mpwo on so.sales_order_id = mpwo.sales_order_id
and date_format(so.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and so.status = #{status}
and so.org_id = #{orgId}
and so.sales_order_code like CONCAT('%', #{salesOrderCode}, '%')
and so.customer_order_code like CONCAT('%', #{customerOrderCode}, '%')
and so.contract_code like CONCAT('%', #{contractCode}, '%')
and so.sales_order_id = #{salesOrderId}
and mpwo.work_order_id = #{workOrderId}
and so.sales_user_name like CONCAT('%',#{salesUserName},'%')
and so.order_type like CONCAT('%',#{orderType},'%')
order by so.modified_time desc
