mapper.select:
SELECT,
oso.sales_order_code,bw.warehouse_name,bm.material_code,bm.material_name,bmb.main_unit,bmb.volume,bmb.net_weight,bmb.gross_weight,
bm.material_desc,bm.material_version,osod.order_qty as salesOrderQty,osod.total_order_return_qty as totalOrderReturnQty
su.user_name as create_user_name,sus.user_name as modified_user_name,bo.organization_name
FROM om_sales_return_order_det osrod
LEFT JOIN om_sales_return_order osro ON osro.sales_return_order_id = osrod.sales_return_order_id
LEFT JOIN om_sales_order oso ON oso.sales_order_id = osrod.sales_order_id
LEFT JOIN om_sales_order_det osod ON osod.sales_order_id = oso.sales_order_id AND osod.material_id = osrod.material_id
LEFT JOIN base_warehouse bw ON bw.warehouse_id = osrod.warehouse_id
LEFT JOIN base_material bm ON osrod.material_id = bm.material_id
LEFT JOIN base_material_tab bmb ON bm.material_id = bmb.material_id
LEFT JOIN sys_user su ON osrod.create_user_id = su.user_id
LEFT JOIN sys_user sus ON osrod.modified_user_id = sus.user_id
LEFT JOIN base_organization bo ON osrod.org_id = bo.organization_id


        and osrod.sales_return_order_id = #{salesReturnOrderId}


        and osrod.sales_return_order_det_id = #{salesReturnOrderDetId}


        and osrod.sales_order_det_id = #{salesOrderDetId}


        and osro.sales_return_order_code = #{salesReturnOrderCode}


        and oso.sales_order_code = #{salesOrderCode}


        and oso.sales_order_id = #{salesOrderId}
