mapper.select:
select,sm.material_name, sm.material_code,sm.version,sm.material_desc,spm.product_model_name, ss.supplier_name, bo.organization_name, u.user_name as create_user_name, s.user_name as modified_user_name from fantech_imes.smt_order so left join ocean.sys_user u on so.create_user_id = u.user_id left join ocean.sys_user s on so.modified_user_id = s.user_id left join fantech_imes.smt_material sm on so.material_id = sm.material_id left join fantech_imes.smt_supplier ss on so.supplier_id = ss.supplier_id LEFT JOIN fantech_imes.base_tab bt on bt.material_id = sm.material_id left join fantech_imes.smt_product_model spm on spm.product_model_id = bt.product_model_id LEFT JOIN fantech_imes.base_organization bo ON so.organization_id = bo.organization_id
and date_format(so.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and so.order_code like CONCAT('%', #{orderCode}, '%')
and so.contract_code like CONCAT('%', #{contractCode}, '%')
and so.material_id=#{materialId}
and sm.material_desc like CONCAT('%', #{materialDesc}, '%')
and ss.supplier_name like CONCAT('%', #{supplierName}, '%')
and so.status=#{status}
and (so.schedule_status= '0' or so.schedule_status= '1')
and so.schedule_status=#{scheduleStatus}
AND so.is_delete = 1
order by so.modified_time desc

SELECT mom.order_material_id, mom.organization_id, mom.order_id, mom.material_id, mom.total, mom.box_code, mom.package_specification_id, mom.size, mom.rough_weight, mom.net_weight, mom.spec_file, mom.schedule_status, mom.is_delete, mom.remark, mom.create_user_id, mom.create_time, mom.modified_user_id, mom.modified_time, su1.user_name AS 'createUserName', su2.user_name AS 'modifiedUserName', spm.product_model_name AS 'productModelName', sm.material_code AS 'materialCode', sm.version AS 'version', sm.material_desc AS 'materialDesc', spu.packing_unit_name as 'packingUnitName' FROM fantech_imes.mes_order_material AS mom LEFT JOIN ocean.sys_user su1 ON su1.user_id = mom.create_user_id LEFT JOIN ocean.sys_user su2 ON su2.user_id = mom.modified_user_id LEFT JOIN fantech_imes.base_organization bo ON mom.organization_id = bo.organization_id LEFT JOIN fantech_imes.smt_material sm ON sm.material_id = mom.material_id LEFT JOIN fantech_imes.base_tab bt on bt.material_id = sm.material_id LEFT JOIN fantech_imes.smt_product_model spm ON spm.product_model_id = bt.product_model_id left join smt_package_specification sps on mom.package_specification_id = sps.package_specification_id left join smt_packing_unit spu on spu.packing_unit_id=sps.packing_unit_id
and mom.order_id = #{orderId}
and mom.order_material_id = #{orderMaterialId}
and mom.schedule_status = #{scheduleStatus}
AND mom.is_delete = 1
ORDER BY mom.modified_time desc
